package clock

import (
	"time"

	"github.com/iotaledger/hive.go/runtime/event"
)

type Events struct {
	// AcceptedTimeUpdated is triggered when the Acceptance Tangle Time advances.
	AcceptedTimeUpdated *event.Event1[time.Time]

	// ConfirmedTimeUpdated is triggered when the Confirmation Tangle Time advances.
	ConfirmedTimeUpdated *event.Event1[time.Time]

	// LinkableCollection is a generic trait that allows to link multiple collections of events together.
	event.Group[Events, *Events]
}

// NewEvents contains the constructor of the Events object (it is generated by a generic factory).
var NewEvents = event.CreateGroupConstructor(func() (newEvents *Events) {
	return &Events{
		AcceptedTimeUpdated:  event.New1[time.Time](),
		ConfirmedTimeUpdated: event.New1[time.Time](),
	}
})
